document.addEventListener('DOMContentLoaded', () => { const galleryImages = document.querySelectorAll('.gallery img'); const modal = document.createElement('div'); const modalImg = document.createElement('img'); const closeModal = document.createElement('span'); modal.classList.add('modal'); closeModal.classList.add('close'); closeModal.innerHTML = '&times;'; modal.appendChild(closeModal); modal.appendChild(modalImg); document.body.appendChild(modal); galleryImages.forEach(image => { image.addEventListener('click', () => { modalImg.src = image.src; modal.style.display = 'block'; }); }); closeModal.addEventListener('click', () => { modal.style.display = 'none'; }); modal.addEventListener('click', () => { modal.style.display = 'none'; }); });